mapper.select:
SELECT * FROM comment_t
WHERE b_idx = #{b_idx} 
ORDER BY C_IDX DESC

SELECT * FROM (
	SELECT rownum r_num,a.* FROM (SELECT * FROM bbs_t
					 WHERE bname = #{bname}

				AND status = 0 ORDER BY b_idx DESC
				AND status = 0 ORDER BY b_idx DESC
				AND status = 0 ORDER BY b_idx DESC
				AND status = 5 or status = 4 ORDER BY status DESC
				AND status = 0 ORDER BY b_idx DESC


	) a
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE bbs_t
SET status = 1
WHERE b_idx = #{no}

UPDATE bbs_t
SET hit = hit+1
WHERE b_idx = #{no}

UPDATE bbs_t
SET
	subject = #{subject},
	content = #{content}

		,file_name = #{file_name}
		,ori_name = #{ori_name}

WHERE b_idx = #{b_idx}

UPDATE bbs_t
SET status = #{status}
WHERE b_idx = #{b_idx}
