mapper.update:
UPDATE u_member
SET stat = -1
WHERE id = #{id}

mapper.select:
SELECT * FROM(
	SELECT rownum r_num, a.* FROM(SELECT * FROM TRANING
				WHERE stat = 1
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

SELECT * FROM(
	SELECT rownum r_num, a.* FROM(SELECT * FROM Like_t
				WHERE stat = 1
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}
