mapper.update:
UPDATE u_member
SET stat = -1
WHERE id = #{id}

mapper.select:
SELECT * FROM(
	SELECT rownum r_num, a.* FROM(SELECT * FROM TRANING
				WHERE stat = 1
				ORDER BY TRANING_id DESC
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

