mapper.select:
	SELECT COUNT(*) FROM u_member
	
  		WHERE stat = #{stat} AND id = #{value}
  	
  	
  		WHERE stat = #{stat} AND name = #{value}
  	
  	
  		WHERE stat = #{stat} AND email = #{value}
  	
  	
  		WHERE stat = #{stat} AND addr = #{value}
  	
  	
  		WHERE stat = #{stat} AND stat = #{value}
  	
  	
  		WHERE stat = #{stat} AND u_date = #{value}

SELECT * FROM(
  	SElECT rownum r_num,a.* FROM (
	  	SELECT * FROM u_member
	  	WHERE stat = #{stat}
	  	ORDER BY id DESC
	) a 
) WHERE r_num BETWEEN #{begin} AND #{end}

SELECT * FROM(
  	SElECT rownum r_num,a.* FROM (
	  	SELECT * FROM u_member
	  	
	  		WHERE stat = #{stat} AND id = #{value}
	  	
	  	
	  		WHERE stat = #{stat} AND name = #{value}
	  	
	  	
	  		WHERE stat = #{stat} AND email = #{value}
	  	
	  	
	  		WHERE stat = #{stat} AND addr = #{value}
	  	
	  	
	  		WHERE stat = #{stat} AND stat = #{value}
	  	
	  	
	  		WHERE stat = #{stat} AND u_date = #{value}
	  	
	  	ORDER BY id DESC
	) a 
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE u_member
SET stat = '-1'
